package code.name.player.musicplayer.ui.activitiesimport android.content.ActivityNotFoundExceptionimport android.content.Intentimport android.net.Uriimport android.os.Bundleimport android.view.Viewimport android.widget.LinearLayoutimport androidx.appcompat.app.AppCompatActivityimport androidx.core.app.ActivityCompatimport com.facebook.ads.AdSizeimport com.facebook.ads.AdViewimport com.facebook.ads.AudienceNetworkAdsimport kotlinx.android.synthetic.main.activity_error_handler.*class ErrorHandlerActivity : AppCompatActivity() {    private var adView: AdView? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(code.name.player.musicplayer.R.layout.activity_error_handler)        // Initialize the Audience Network SDK        AudienceNetworkAds.initialize(this);        // Instantiate an AdView object.        // NOTE: The placement ID from the Facebook Monetization Manager identifies your App.        // To get test ads, add IMG_16_9_APP_INSTALL# to your placement id. Remove this when your app is ready to serve real ads.        // Instantiate an AdView object.        // NOTE: The placement ID from the Facebook Monetization Manager identifies your App.        // To get test ads, add IMG_16_9_APP_INSTALL# to your placement id. Remove this when your app is ready to serve real ads.        adView = AdView(this, "266586284404690_267134047683247", AdSize.BANNER_HEIGHT_50)        // Find the Ad Container        val adContainer = findViewById<View>(code.name.player.musicplayer.R.id.error_handler) as LinearLayout        // Add the ad view to your activity layout        // Add the ad view to your activity layout        adContainer.addView(adView)        // Request an ad        // Request an ad        adView!!.loadAd()        clearAppData.setOnClickListener {            try {                val intent = Intent(android.provider.Settings.ACTION_APPLICATION_DETAILS_SETTINGS)                intent.data = Uri.parse("package:$packageName")                startActivity(intent)            } catch (e: ActivityNotFoundException) {                val intent = Intent(android.provider.Settings.ACTION_MANAGE_APPLICATIONS_SETTINGS)                startActivity(intent)            }        }        restart.setOnClickListener {            //Toast.makeText(activity, activity.getResources().getString(R.string.no_equalizer),            //Toast.LENGTH_SHORT).show();            ActivityCompat.startActivity(this, Intent(this, MainActivity::class.java), null)        }        showCrashError.text = String.format("%s", intent.getStringExtra("error"))    }    override fun onDestroy() {        if (adView != null) adView!!.destroy()        super.onDestroy()    }}